import React, { useState } from "react";

function Nav() {
    const [choices] = useState([
      { name: "About me" },
      { name: "Portefolio" },
      { name: "Contact" },
      { name: "Resume" },
    ]);
    const [currentChoice, setCurrentChoice] = useState(choices[0]);
  return (
    <header>
      <h1 className="name">Rayan Diouf</h1>
      <nav>
        <ul className="navbar">
          {choices.map((choice) => (
            <li
              className={`mx-1 ${
                currentChoice.name === choice.name && "navActive"
              }`}
              key={choice.name}
            >
              <span
                onClick={() => {
                  setCurrentChoice(choice);
                }}
              ></span>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
            }
export default Nav;
